import osfrom datetime import datetime,timedeltathr=(datetime.now()-timedelta(minutes=2)).timestamp()def dispensor(path,thr,base=True):    if(os.path.isdir(path)):        for i in os.listdir(path):            dispensor(path+ '\\' + i,thr,False)        if len(os.listdir(path))==0:            os.removedirs(path)    else:        time=os.path.getmtime(path)        if time < thr:            os.remove((path))            print('Old file deleted')        else:            print('NO file deleted')path='jlknf.txt'dispensor(path,thr)